import React, { Component } from 'react';

import './NumberSeries.css';

class NumberSeries extends Component {

    constructor(props) {
        super(props);
        
		this.state = {
			name : null,
			weather : null,
			icon : null,
			temp : 0,
			list : [],
			city : null,
			isForecastShowed : false,
			loaderClass : 'hide',
			message : 'no data'
		}
	
		/*this.handleChange = this.handleChange.bind(this);
		this.showForcast = this.showForcast.bind(this);*/
	}

	render() {
		return (
			<section className="number-series">

                <p>Is your number in the next series of numbers?</p>
                
                <article>

                </article>

                <button className="btn btn-no">NO</button>
                <button className="btn btn-yes">YES</button>
                
            </section>
		);
  	}
}

export default NumberSeries;
