import React, { Component } from 'react';
import './App.css';

import StartScreen from '../components/StartScreen/Start';
import PlayScreen from '../components/PlayScreen/Play';
import EndScreen from '../components/EndScreen/End';

class App extends Component {

	constructor(props) {

		super(props);

		this.state = {
			isStartClicked : false,
			isEnd : false
		}

        this.clickStart = this.clickStart.bind(this);

	}

	clickStart = () => {
		this.setState({
			isStartClicked : true
		});
	}

	clickYesNo = () => {
		this.setState({
			isEnd : false
		})
	}

	render() {

		let data;

		if(this.state.isStartClicked && !this.state.isEnd) {
			data = <PlayScreen />;

		} else if(!this.state.isStartClicked) {
			data = <StartScreen clicked={this.clickStart}/>;
		} else {
			data = <EndScreen />
		}

		return (
			<div className="App">
				{data}
			</div>
		);
  	}
}

export default App;
